[asy]
  import graph;
  unitsize(10);

  real f(real x)
  {
    return sqrt(cbrt(x^4) - 4 x^2 + 4);
  }

  real g(real x)
  {
    return (cbrt(x^2) + f(x))/2;
  }

  real h(real x)
  {
